import React, { useEffect, useState } from 'react';

function Meeting() {
  const username = localStorage.getItem('username');
  const userType = localStorage.getItem('userType');

  const [meetIdList, setMeetIdList] = useState([]);
  const [form, setForm] = useState({
    meet_id: '',
    date: '',
    link: '',
    place: '',
    reason: '',
    reqStatus: userType === 'admin' ? 'done' : 'pending'
  });

  useEffect(() => {
    fetch('http://localhost:5000/api/meeting-ids')
      .then(res => res.json())
      .then(data => setMeetIdList(data))
      .catch(err => console.error("❌ Failed to load meet IDs:", err));
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/set-meeting', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...form, username })
      });

      const result = await res.json();
      alert(result.message || 'Meeting submitted');
    } catch (err) {
      console.error("❌ Submission failed:", err);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Set Meeting</h2>

      <div>
        <label>Choose or Add Meet ID:</label><br />
        <select
          name="meet_id"
          value={form.meet_id}
          onChange={handleChange}
        >
          <option value="">-- Select or type below --</option>
          {meetIdList.map((id, idx) => (
            <option key={idx} value={id}>{id}</option>
          ))}
        </select>
        <br /><input
          type="text"
          placeholder="Or enter new Meet ID"
          name="meet_id"
          value={form.meet_id}
          onChange={handleChange}
        />
      </div>

      <br />
      <input type="date" name="date" value={form.date} onChange={handleChange} placeholder="Date" /><br /><br />
      <input type="text" name="link" value={form.link} onChange={handleChange} placeholder="Meeting Link" /><br /><br />
      <input type="text" name="place" value={form.place} onChange={handleChange} placeholder="Place" /><br /><br />
      <input type="text" name="reason" value={form.reason} onChange={handleChange} placeholder="Reason" /><br /><br />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default Meeting;
