import React, { useEffect, useState } from 'react';

function Profile() {
  const username = localStorage.getItem('username');
  const [profile, setProfile] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    fetch(`http://localhost:5000/api/profile/${username}`)
      .then(res => res.json())
      .then(data => {
        setProfile(data);
        setFormData(data);
      });
  }, [username]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    await fetch('http://localhost:5000/api/profile', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, ...formData })
    });
    setProfile(formData);
    setEditMode(false);
  };

  const renderField = (label, key) => {
    return (
      <div style={{ marginBottom: '10px' }}>
        <strong>{label}:</strong>{' '}
        {editMode ? (
          <input
            type="text"
            name={key}
            value={formData[key] || ''}
            onChange={handleChange}
          />
        ) : profile?.[key] ? (
          profile[key]
        ) : (
          <em>Add info</em>
        )}
      </div>
    );
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>User Profile</h2>
      {renderField("Name", "name")}
      {renderField("Email", "email")}
      {renderField("Profession", "profession")}
      {renderField("House Number", "houseNumber")}
      {renderField("Address", "address")}
      {renderField("Owner/Renter", "ownershipStatus")}
      {renderField("Residing From", "residingFrom")}
      {renderField("Contact Info", "contactInfo")}
      {renderField("Additional Info", "additionalInfo")}

      <br />
      {editMode ? (
        <>
          <button onClick={handleSubmit}>Save</button>{' '}
          <button onClick={() => setEditMode(false)}>Cancel</button>
        </>
      ) : (
        <button onClick={() => setEditMode(true)}>
          {profile ? 'Edit' : 'Add Info'}
        </button>
      )}
    </div>
  );
}

export default Profile;
