import React, { useState, useEffect } from 'react';

function AdminSpace() {
  const [showForm, setShowForm] = useState(false);
  const [message, setMessage] = useState('');
  const [formData, setFormData] = useState({
    username: '', password: '', name: '', email: '', profession: '',
    houseNumber: '', address: '', ownershipStatus: '', residingFrom: '',
    contactInfo: '', additionalInfo: '',
  });

  // üü° State for meeting requests
  const [pendingMeetings, setPendingMeetings] = useState([]);

  // üîÑ Fetch pending meetings
  const fetchPendingMeetings = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/admin/pending-meetings');
      const data = await res.json();
      setPendingMeetings(data);
    } catch (err) {
      console.error('‚ùå Failed to load meetings:', err);
    }
  };

  useEffect(() => {
    fetchPendingMeetings();
  }, []);

  // ‚úÖ Accept meeting
  const handleAccept = async (_id) => {
    await fetch('http://localhost:5000/api/admin/accept-meeting', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ _id })
    });
    fetchPendingMeetings();
  };

  // ‚ùå Reject meeting
  const handleReject = async (_id) => {
    await fetch('http://localhost:5000/api/admin/reject-meeting', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ _id })
    });
    fetchPendingMeetings();
  };

  // Form input handler
  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // Submit member form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/admin/add-member', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (data.status === 'success') {
        setMessage('‚úÖ Member added successfully.');
        setFormData({
          username: '', password: '', name: '', email: '', profession: '',
          houseNumber: '', address: '', ownershipStatus: '', residingFrom: '',
          contactInfo: '', additionalInfo: '',
        });
        setShowForm(false);
      } else {
        setMessage(`‚ùå Error: ${data.message}`);
      }
    } catch (error) {
      console.error('Submit error:', error);
      setMessage('‚ùå Network error. Could not submit.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Admin Space</h2>

      {/* üîπ Add Members Section */}
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Cancel' : 'Add Members'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
          {[
            { label: 'Username', name: 'username' },
            { label: 'Password', name: 'password', type: 'password' },
            { label: 'Name', name: 'name' },
            { label: 'Email', name: 'email', type: 'email' },
            { label: 'Profession', name: 'profession' },
            { label: 'House Number', name: 'houseNumber' },
            { label: 'Address', name: 'address' },
            { label: 'Owner/Renter', name: 'ownershipStatus' },
            { label: 'Residing From', name: 'residingFrom', type: 'date' },
            { label: 'Contact Info', name: 'contactInfo' },
            { label: 'Additional Info', name: 'additionalInfo' },
          ].map(({ label, name, type = 'text' }) => (
            <div key={name} style={{ marginBottom: '10px' }}>
              <label>{label}: </label>
              <input
                type={type}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                required={name !== 'additionalInfo'}
              />
            </div>
          ))}
          <button type="submit">Submit</button>
        </form>
      )}

      {message && <p style={{ marginTop: '20px', color: 'green' }}>{message}</p>}

      {/* üîπ Meeting Request Section */}
      <hr style={{ margin: '2rem 0' }} />
      <h3>üóìÔ∏è Pending Meeting Requests</h3>
      {pendingMeetings.length === 0 ? (
        <p>No pending meetings.</p>
      ) : (
        pendingMeetings.map((meeting) => (
          <div key={meeting._id} style={{
            border: '1px solid #ccc',
            margin: '1rem 0',
            padding: '1rem',
            borderRadius: '8px'
          }}>
            <p><strong>Meet ID:</strong> {meeting.meet_id}</p>
            <p><strong>Date:</strong> {meeting.date}</p>
            <p><strong>Link:</strong> {meeting.link}</p>
            <p><strong>Place:</strong> {meeting.place}</p>
            <p><strong>Reason:</strong> {meeting.reason}</p>
            <p><strong>Requested by:</strong> {meeting.username}</p>
            <p><strong>Status:</strong> {meeting.req}</p>
            <button onClick={() => handleAccept(meeting._id)}>‚úÖ Accept</button>{' '}
            <button onClick={() => handleReject(meeting._id)}>‚ùå Reject</button>
          </div>
        ))
      )}
    </div>
  );
}

export default AdminSpace;
