import React, { useState } from 'react';

function AdminSpace() {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    email: '',
    profession: '',
    houseNumber: '',
    address: '',
    ownershipStatus: '',
    residingFrom: '',
    contactInfo: '',
    additionalInfo: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch('http://localhost:5000/api/admin/add-member', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    });

    const data = await res.json();
    if (data.status === 'success') {
      setMessage("✅ Member added successfully.");
      setFormData({
        username: '',
        password: '',
        name: '',
        email: '',
        profession: '',
        houseNumber: '',
        address: '',
        ownershipStatus: '',
        residingFrom: '',
        contactInfo: '',
        additionalInfo: '',
      });
      setShowForm(false);
    } else {
      setMessage("❌ Failed to add member.");
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Admin Space</h2>
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? "Cancel" : "Add Members"}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
          {[
            "username", "password", "name", "email", "profession",
            "houseNumber", "address", "ownershipStatus",
            "residingFrom", "contactInfo", "additionalInfo"
          ].map(field => (
            <div key={field} style={{ marginBottom: '10px' }}>
              <label>{field.replace(/([A-Z])/g, ' $1')}: </label>
              <input
                type={field === "residingFrom" ? "date" : "text"}
                name={field}
                value={formData[field]}
                onChange={handleChange}
                required={field !== "additionalInfo"} // optional field
              />
            </div>
          ))}
          <button type="submit">Submit</button>
        </form>
      )}

      {message && <p style={{ marginTop: '20px', color: 'green' }}>{message}</p>}
    </div>
  );
}

export default AdminSpace;
