import React, { useState } from 'react';

function AdminSpace() {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    name: '',
    email: '',
    profession: '',
    houseNumber: '',
    address: '',
    ownershipStatus: '',
    residingFrom: '',
    contactInfo: '',
    additionalInfo: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/admin/add-member', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (data.status === 'success') {
        setMessage('✅ Member added successfully.');
        setFormData({
          username: '',
          password: '',
          name: '',
          email: '',
          profession: '',
          houseNumber: '',
          address: '',
          ownershipStatus: '',
          residingFrom: '',
          contactInfo: '',
          additionalInfo: '',
        });
        setShowForm(false);
      } else {
        setMessage(`❌ Error: ${data.message}`);
      }
    } catch (error) {
      console.error('Submit error:', error);
      setMessage('❌ Network error. Could not submit.');
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Admin Space</h2>
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Cancel' : 'Add Members'}
      </button>

      {showForm && (
        <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
          {[
            { label: 'Username', name: 'username' },
            { label: 'Password', name: 'password', type: 'password' },
            { label: 'Name', name: 'name' },
            { label: 'Email', name: 'email', type: 'email' },
            { label: 'Profession', name: 'profession' },
            { label: 'House Number', name: 'houseNumber' },
            { label: 'Address', name: 'address' },
            { label: 'Owner/Renter', name: 'ownershipStatus' },
            { label: 'Residing From', name: 'residingFrom', type: 'date' },
            { label: 'Contact Info', name: 'contactInfo' },
            { label: 'Additional Info', name: 'additionalInfo' },
          ].map(({ label, name, type = 'text' }) => (
            <div key={name} style={{ marginBottom: '10px' }}>
              <label>{label}: </label>
              <input
                type={type}
                name={name}
                value={formData[name]}
                onChange={handleChange}
                required={name !== 'additionalInfo'}
              />
            </div>
          ))}
          <button type="submit">Submit</button>
        </form>
      )}

      {message && <p style={{ marginTop: '20px', color: 'green' }}>{message}</p>}
    </div>
  );
}

export default AdminSpace;
